                                                                                                                                                                                                                                     //checkContinue();                                                                                                                        }),NULL));            }        }    });    addChild(answerButton1);    addChild(answerButton2);    addChild(answerButton3);    addChild(answerButton4);    addChild(pause);        auto timeSpriteBg = Sprite::create(StringUtils::format("game/tbgb%d.png",currentSeason));    timeSpriteBg->setPosition(Vec2(visibleSize.width/2.0,visibleSize.height-timeSpriteBg->getContentSize().height*3.0/5.0));    addChild(timeSpriteBg);        auto timeSprite = Sprite::create(StringUtils::format("game/tb%d.png",currentSeason));    timeBar = ProgressTimer::create(timeSprite);    timeBar->setType(ProgressTimer::Type::BAR);    timeBar->setMidpoint(Vec2(0,0));    timeBar->setBarChangeRate(Vec2(1,0));    timeBar->setPercentage(100);    timeBar->setPosition(Vec2(visibleSize.width/2.0,visibleSize.height-timeBar->getContentSize().height*12.0/10.0));    addChild(timeBar);    timeBar->setReverseProgress(false);    schedule(schedule_selector(GameScene::updateTime),1.0/60.0);        auto timeicon= Sprite::create("game/timeicon.png");    timeicon->setPosition(Vec2(visibleSize.width*2.0/10.0+timeicon->getContentSize().width/2.0,visibleSize.height-timeicon->getContentSize().height/2.0));    addChild(timeicon);            item1 = Button::create("game/d1s.png");    if(item1Number==0){        item1->setColor(Color3B(50,50,50));    }    item1->setPosition(Vec2(visibleSize.width*5.0/10.0,item1->getContentSize().height*6.0/10.0));    item1->addTouchEventListener([&](Ref* pSender,Widget::TouchEventType type){        if(type==Widget::TouchEventType::ENDED){            if(talkbg!=NULL&&talkbg->isVisible()){                return ;            }            if(right!=NULL&& right->isVisible()){                return ;            }            if(wrong!=NULL&& wrong->isVisible()){                return ;            }            if(item1Number>0&&currentQuestionDeleteAnswerNumber<3){                reduceAWrongAnswer();                item1Number--;                item1NumberIcon->setTitleText(StringUtils::format("%d",item1Number).c_str());                if(item1Number==0){                    item1->setColor(Color3B(50,50,50));                }            }        }    });    addChild(item1);    item2 = Button::create("game/d2s.png");    if(item2Number==0){        item2->setColor(Color3B(50,50,50));    }    item2->setPosition(Vec2(visibleSize.width*3.5/10.0,item2->getContentSize().height*6.0/10.0));    item2->addTouchEventListener([&](Ref* pSender,Widget::TouchEventType type){        if(type==Widget::TouchEventType::ENDED){            if(talkbg!=NULL&&talkbg->isVisible()){                return ;            }            if(right!=NULL&& right->isVisible()){                return ;            }            if(wrong!=NULL&& wrong->isVisible()){                return ;            }            if(item2Number>0){                addTenSeconds();                item2Number--;                 item2NumberIcon->setTitleText(StringUtils::format("%d",item2Number).c_str());                if(item2Number==0){                    item2->setColor(Color3B(50,50,50));                }            }        }    });    addChild(item2);    item3 = Button::create("game/d3s.png");    if(item3Number==0){        item3->setColor(Color3B(50,50,50));    }    item3->setPosition(Vec2(visibleSize.width*6.5/10.0,item3->getContentSize().height*6.0/10.0));    item3->addTouchEventListener([&](Ref* pSender,Widget::TouchEventType type){        if(type==Widget::TouchEventType::ENDED){            if(talkbg!=NULL&&talkbg->isVisible()){            return ;    